import React, {useRef} from 'react'

const IncomeForm = ({income, setIncome}) => {
    const desc = useRef(null);
    const AddIncome = (e) => {
        e.preventDefault();
        console.log(desc.current.value);
    }

    return (
        <form className='income-form' onSubmit={AddIncome}>
            <div className='form-inner'>
                <input type="text" name="decs" id="desc" placeholder='Enter Income description' ref={desc}/>
                <input type="number" name="price" id="price" placeholder='Enter Amount'/>
                <input type="date" name="date" id="date" placeholder='Enter income date'/>
                <input type="submit" value="Add income"/>

            </div>

        </form>
    )
}

export default IncomeForm;
