import React, {useRef} from 'react'

const IncomeForm = ({income, setIncome}) => {
    const desc = useRef(null);
    const price = useRef(null);
    const date = useRef(null);

    const AddIncome = e => {
        e.preventDefault();

        let d = date.current.value.split("-");
        let newD = new Date (d[0], d[1] - 1, d[2]);

        setIncome([...income, {
            "desc" : desc.current.value,
            "price" : price.current.value,
            "date" : newD.getTime()
        }]);

        desc.current.value = "";
        date.current.value = null;
        price.current.value = null;
    }

    return (
        <form className='income-form' onSubmit={AddIncome}>
            <div className='form-inner'>
                <input type="text" name="decs" id="desc" placeholder='Enter Income description' ref={desc}/>
                <input type="number" name="price" id="price" placeholder='Enter Amount' ref={price}/>
                <input type="date" name="date" id="date" placeholder='Enter income date' ref={date}/>
                <input type="submit" value="Add income"/>

            </div>

        </form>
    )
}

export default IncomeForm;
